import os
import base64
import customtkinter as ctk
from customtkinter import CTk, CTkLabel, CTkButton, CTkFrame
from localization import LocalizationTool
from comparator import FileComparator
from converter import BilingualConverter

# Base64 编码的图标数据（将你的 .ico 文件转换为 Base64 字符串后填在这里）
icon_data = ""

ctk.set_appearance_mode("Dark")
ctk.set_default_color_theme("dark-blue")

class PixelComposerTool(CTk):
    def __init__(self):
        super().__init__()
        self.title("PixelComposer工具")
        self.geometry("800x600")
        self.resizable(False, False)
        self._setup_ui()
        self._set_window_icon()
        self._center_window()

    def _setup_ui(self):
        self.grid_columnconfigure(1, weight=1)
        self.grid_rowconfigure(0, weight=1)

        # 侧边栏
        self.sidebar_frame = CTkFrame(self, width=140, corner_radius=0)
        self.sidebar_frame.grid(row=0, column=0, rowspan=4, sticky="nsew")
        
        button_style = {
            "fg_color": "#FF9166",
            "hover_color": "#E57C52",
            "font": ("Microsoft YaHei", 14, "bold"),
            "text_color": "#191925",
            "corner_radius": 8
        }
        
        tools = [
            ("汉化工具", self.show_localization_tool),
            ("文件对比工具", self.show_comparator_tool),
            ("双语转换工具", self.show_converter_tool)
        ]
        
        for text, cmd in tools:
            CTkButton(self.sidebar_frame, text=text, command=cmd, **button_style).pack(pady=10, padx=20, fill='x')

        CTkLabel(self.sidebar_frame, 
                text="作者: 安尘,WWNNL", 
                font=("Microsoft YaHei", 12), 
                text_color="#777777").pack(side="bottom", pady=10)

        self.main_frame = CTkFrame(self)
        self.main_frame.grid(row=0, column=1, sticky="nsew", padx=20, pady=20)

    def _set_window_icon(self):
        if os.name == 'nt' and icon_data:
            try:
                # 将 Base64 编码的图标数据解码并设置为窗口图标
                icon_bytes = base64.b64decode(icon_data.strip())
                with open("temp_icon.ico", "wb") as f:
                    f.write(icon_bytes)
                self.iconbitmap("temp_icon.ico")
                os.remove("temp_icon.ico")
            except Exception as e:
                print(f"设置图标时出现错误: {e}")

    def _center_window(self):
        # 获取屏幕宽度和高度
        screen_width = self.winfo_screenwidth()
        screen_height = self.winfo_screenheight()
        
        # 计算窗口位置
        x = (screen_width - 800) // 2
        y = (screen_height - 600) // 2
        
        # 设置窗口位置
        self.geometry(f"+{x}+{y}")

    def show_localization_tool(self):
        self._clear_main_frame()
        LocalizationTool(self.main_frame).pack(fill="both", expand=True)

    def show_comparator_tool(self):
        self._clear_main_frame()
        FileComparator(self.main_frame).pack(fill="both", expand=True)

    def show_converter_tool(self):
        self._clear_main_frame()
        BilingualConverter(self.main_frame).pack(fill="both", expand=True)

    def _clear_main_frame(self):
        for widget in self.main_frame.winfo_children():
            widget.destroy()

if __name__ == "__main__":
    app = PixelComposerTool()
    app.mainloop()